import { defineNuxtModule, createResolver, addPlugin, addComponentsDir, addImportsDir } from '@nuxt/kit';

const module = defineNuxtModule({
  meta: {
    name: "@ringzero/nuxt3-front-template-set",
    configKey: "nuxt3FrontTemplateSet"
  },
  // Default configuration options of the Nuxt module
  defaults: {},
  async setup(options, nuxt) {
    const resolver = createResolver(import.meta.url);
    addPlugin(resolver.resolve("./runtime/plugins/vuetify"));
    addPlugin(resolver.resolve("./runtime/plugins/pinia"));
    addPlugin(resolver.resolve("./runtime/plugins/dayjs"));
    addComponentsDir({
      path: resolver.resolve("./runtime/components")
    });
    addImportsDir(resolver.resolve("./runtime/composables"));
    addImportsDir(resolver.resolve("./runtime/assets"));
    addImportsDir(resolver.resolve("./runtime/helpers"));
  }
});

export { module as default };
