<template>
  <div
    class="ButtonToggleSet"
    :class="{
      inline: inline,
      small: small,
    }"
  >
    <div v-if="label" class="FormLabel">{{ label }}</div>
    <div
      class="ToggleSet"
      :class="{
        inline: inline,
        small: small,
      }"
      :style="{
        backgroundColor: useGetThemeColor(
          lineColor ? lineColor : dark !== undefined ? (dark ? '#555' : '#CCC') : isDark() ? '#555' : '#CCC'
        ),
        borderColor: useGetThemeColor(
          lineColor ? lineColor : dark !== undefined ? (dark ? '#555' : '#CCC') : isDark() ? '#555' : '#CCC'
        ),
      }"
    >
      <div
        v-for="(item, i) in items"
        class="ToggleButton"
        :key="i"
        :class="{
          Active: input === item.value,
        }"
        :style="{
          backgroundColor: dark !== undefined ? (dark ? '#111' : '#fff') : isDark() ? '#111' : '#fff',

          color:
            input === item.value
              ? useGetThemeColor(item.activeColor || activeColor || '#fff')
              : useGetThemeColor(
                  item.color || color || dark !== undefined ? (dark ? '#AAA' : '#999') : isDark() ? '#AAA' : '#999'
                ),
          width: width ? width + 'px' : undefined,
        }"
        @click="onClick(item)"
      >
        <div
          class="Back"
          :style="{
            backgroundColor:
              input === item.value
                ? useGetThemeColor(item.activeBack || activeBackColor || 'primary')
                : useGetThemeColor(
                    item.back || backColor || dark !== undefined ? (dark ? '#444' : '#eee') : isDark() ? '#444' : '#eee'
                  ),
            opacity: input === item.value ? 1 : undefined,
          }"
        />
        <div
          v-if="item.icon"
          class="Icon"
          :style="{
            marginTop: item.icon ? item.icon : undefined,
          }"
        >
          <v-icon :icon="item.icon" />
        </div>
        <div v-if="item.text" class="Label mr-1 ml-1">{{ item.text }}</div>
      </div>
    </div>
  </div>
</template>

<script setup lang="ts">
import { ref, watch } from 'vue'
import type { ToggleSetProps } from '../../components/common'
import { isDark, useGetThemeColor } from '../../composables/colors'

// props
interface Props {
  items?: ToggleSetProps[]
  value: string | number
  activeBackColor?: string
  activeColor?: string
  backColor?: string
  color?: string
  inline?: boolean
  width?: number
  small?: boolean
  dark?: boolean
  lineColor?: string
  label?: string
}

// propsのデフォルト値設定
const props = withDefaults(defineProps<Props>(), {
  value: 'test',
  inline: false,
  dark: undefined,
})

//------------------------------------------------------------------------------------------------------------
// ライフサイクル
//------------------------------------------------------------------------------------------------------------

const emits = defineEmits<{ (e: 'update:value', item: ToggleSetProps): void }>()

//state
const input = ref(props.value)

function onClick(item: ToggleSetProps) {
  input.value = item.value
  emits('update:value', item)
}

watch(
  () => props.value,
  (value) => {
    input.value = value
  }
)
</script>

<style scoped>
.ButtonToggleSet {
  display: flex;
  align-items: center;
}
.ButtonToggleSet.inline {
  flex-direction: column;
  justify-content: center;
  align-items: center;
}
.ButtonToggleSet.inline .FormLabel {
  margin-right: 0px;
}

.FormLabel {
  font-size: 14px;
  margin-right: 10px;
}

.ToggleSet {
  position: relative;
  display: flex;
  border: 2px solid #ccc;
  background: #ccc;
  padding: 0;
  border-radius: 5px;
  overflow: hidden;
  cursor: pointer;
}
.ToggleSet .ToggleButton {
  position: relative;
  font-size: 12px;
  height: 32px;
  font-weight: bold;
  padding: 0px 8px;
  margin-right: 2px;
  display: flex;
  justify-content: center;
  align-items: center;
}
.ToggleSet .ToggleButton.Active .Back {
  opacity: 1;
}
.ToggleSet .ToggleButton.Active .Label {
  opacity: 1;
}
.ToggleSet .ToggleButton.Active:hover .Back {
  opacity: 1;
}
.ToggleSet .ToggleButton.Active:hover .Label {
  opacity: 1;
}
.ToggleSet .ToggleButton:hover .Back {
  opacity: 0.5;
}
.ToggleSet .ToggleButton:hover .Label {
  opacity: 1;
}
.ToggleSet .ToggleButton .Back {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  transition: opacity 0.2s ease;
}
.ToggleSet .ToggleButton .Icon {
  position: relative;
  transition: opacity 0.2s ease;
}
.ToggleSet .ToggleButton .Label {
  position: relative;
  opacity: 0.5;
  transition: opacity 0.2s ease;
}
.ToggleSet .ToggleButton:first-child {
  border-radius: 4px 0 0 4px;
}
.ToggleSet .ToggleButton:last-child {
  border-radius: 0 4px 4px 0;
  margin-right: 0px;
}
.ToggleSet.inline {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}
.ToggleSet.inline .ToggleButton {
  margin-right: 0px;
  margin-bottom: 2px;
}
.ToggleSet.inline .ToggleButton:first-child {
  border-radius: 5px 5px 0 0;
}
.ToggleSet.inline .ToggleButton:last-child {
  border-radius: 0 0 5px 5px;
  margin-bottom: 0px;
}
.ToggleSet.small {
  border: 2px solid #ccc;
  background: #ccc;
  padding: 0;
  margin-right: 1px;
  border-radius: 5px;
}
.ToggleSet.small .ToggleButton {
  font-size: 10px;
  height: 22px;
  font-weight: bold;
  padding: 1px 4px 0;
}
.ToggleSet.small.inline .ToggleButton {
  margin-right: 0px;
  margin-bottom: 1px;
}
.ToggleSet.small.inline .ToggleButton:first-child {
  border-radius: 4px 4px 0 0;
}
.ToggleSet.small.inline .ToggleButton:last-child {
  border-radius: 0 0 4px 4px;
  margin-bottom: 0px;
}
</style>
