<template>
  <FormBase
    class="FormCheckGroup"
    :base="base"
    :label="label"
    :rule="rule || data?.rule"
    :mode="mode"
    :edit="isEdit"
    :width="width"
    :col-width="colWidth"
    :label-width="labelWidth"
    :hint="hint"
    :hintchip="hintchip"
    :error="inputError"
    :result="result"
    :focus="focus"
    :line="false"
    :small="small"
    :medium="medium"
  >
    <ButtonToggle
      class="CheckButton mr-2"
      :style="{ marginLeft: '-8px', marginTop: '3.5px' }"
      icon="check"
      :value="input"
      :color="color"
      :actived="input"
      :disabled="!isEdit"
      :label="text"
      @update:value="onChange($event)"
    />
  </FormBase>
</template>

<script setup lang="ts">
/**===================================================================================================================
   * チェックグループ
   ===================================================================================================================**/
import { type formProps } from '../../components/common'
import { type CheckBoxSetProps } from '../../components/common'
import { RULE } from '../../composables/validation'
import { ref, watch, onMounted, type Ref } from 'vue'

//------------------------------------------------------------------------------------------------------------
// 引数
//------------------------------------------------------------------------------------------------------------
interface Props extends formProps {
  value?: boolean
  text?: string
  color?: string
  inline?: boolean
  rule?: RULE[]
  items?: CheckBoxSetProps[]
}

const props = withDefaults(defineProps<Props>(), {})
//------------------------------------------------------------------------------------------------------------
// 定数・変数（state）
//------------------------------------------------------------------------------------------------------------
const inputError: Ref<string | undefined> = ref(props.error)
const result: Ref<string | undefined> = ref()
const focus = ref(false)
const isEdit = ref(props.edit || props.base?.edit)
const input = ref(props.value)
//------------------------------------------------------------------------------------------------------------
// ライフサイクル
//------------------------------------------------------------------------------------------------------------
onMounted(() => {})

//------------------------------------------------------------------------------------------------------------
//watch
//------------------------------------------------------------------------------------------------------------
watch(
  () => props.error,
  (value) => {
    inputError.value = value
  }
)

watch(
  () => props.data,
  (value) => {
    if (props.data) {
      // valuesからのchecked設定
      if (props.data.value && props.data.value === 'true') {
        input.value = true
      } else if (props.data.value && props.data.value !== 'false') {
        input.value = true
      } else {
        input.value = false
      }
      if (value?.onCheck === 'check') {
        validation()
        props.data.onCheck = ''
      } else if (value?.onCheck === 'clear') {
        props.data.onCheck = ''
        props.data.error = ''
        inputError.value = ''
      }
      if (value?.error) {
        // inputError.value = value?.error
      }
    }
  },
  {
    deep: true,
  }
)

watch(
  () => props.edit,
  (value) => {
    if (value) {
      isEdit.value = true
    } else {
      isEdit.value = false
    }
  }
)

watch(
  () => props.base,
  (value) => {
    if (value?.edit) {
      isEdit.value = true
    } else {
      isEdit.value = false
    }
  }
)

//------------------------------------------------------------------------------------------------------------
// エミット
//------------------------------------------------------------------------------------------------------------
const emits = defineEmits<{
  (e: 'update:value', value: boolean): void
}>()

function onChange(value: string | number | boolean) {
  //Valueは加工しないとイケない
  if (typeof value === 'boolean') {
    input.value = !value
    if (props.data) {
      props.data.value = input.value.toString()
    }
    props.items?.forEach((item) => {
      item.checked = input.value
    })
    validation()
    emits('update:value', value)
  }
}

function validation() {
  if (props.data) props.data.isCheck = true
  //チェックグループのバリデーションは、必須の場合のみ
  const rule: RULE[] = props.rule || props.data?.rule || []

  if (rule.includes(RULE.required)) {
    let res
    props.items?.forEach((item) => {
      // console.log(item.checked)

      if (item.checked) res = true
    })
    if (res === true) {
      inputError.value = ''
      if (props.data) props.data.error = ''
      return true
    } else {
      inputError.value = ''
      if (props.data) props.data.error = '必須項目です。'
      return false
    }
  }
}

//------------------------------------------------------------------------------------------------------------
// メソッド
//------------------------------------------------------------------------------------------------------------
function onFocus() {
  focus.value = true
}
function onBlur() {
  focus.value = false
}

//------------------------------------------------------------------------------------------------------------
// バリデーション
//------------------------------------------------------------------------------------------------------------
</script>

