<template>
  <FormBase
    class="FormInputText"
    :base="base"
    :label="label"
    :rule="rule || data?.rule"
    :min="min"
    :max="max"
    :mode="mode"
    :edit="isEdit"
    :width="width || base?.width"
    :col-width="colWidth"
    :label-width="labelWidth"
    :hint="hint"
    :hintchip="hintchip"
    :error="isParts ? '' : inputError"
    :result="result"
    :focus="focus"
    :small="small"
    :medium="medium"
  >
    <div class="FormInputContainer">
      <div v-if="prefix" class="Prefix">{{ prefix }}</div>
      <div
        class="FormInputContent"
        :style="{
          width: width ? width + 'px' : base?.width ? base.width + 'px' : '100%',
          height: base?.small || small ? '20px' : base?.medium || medium ? '24px' : '36px',
        }"
      >
        <input
          v-if="isEdit"
          ref="inputItem"
          type="text"
          :id="props.data?.name"
          :name="props.data?.name"
          class="Input"
          :class="{ InputError: inputError, Small: base?.small || small, Medium: base?.medium || medium }"
          :style="{ padding: innerMargin }"
          :value="input"
          :placeholder="placeholder"
          @input="onChange"
          @focus="onFocus"
          @blur="onBlur"
        />
        <input v-else type="text" class="Preview" :style="{ padding: innerMargin }" :value="input" disabled />
      </div>
      <div v-if="suffix" class="Suffix">{{ suffix }}</div>
    </div>
  </FormBase>
</template>

<script setup lang="ts">
/**===================================================================================================================
   * InputText(文字列一行入力)
   ===================================================================================================================**/
import { type formProps } from '../../components/common'
import { RULE, useValidation, type ruleParams } from '../../composables/validation'
import { ref, watch, type Ref } from 'vue'

//------------------------------------------------------------------------------------------------------------
// 引数
//------------------------------------------------------------------------------------------------------------
interface Props extends formProps {
  rule?: any
  value?: string
  innerMargin?: string
  isParts?: boolean
}

const props = withDefaults(defineProps<Props>(), {})
//------------------------------------------------------------------------------------------------------------
// 定数・変数（state）
//------------------------------------------------------------------------------------------------------------
const input: Ref<string | undefined> = ref(props.data?.value || props.value)
const inputError: Ref<string | undefined> = ref(props.error)
const inputItem: Ref<HTMLInputElement | undefined> = ref()
const result: Ref<string | undefined> = ref()
const focus = ref(false)
const isEdit = ref(props.edit || props.base?.edit)

//------------------------------------------------------------------------------------------------------------
// ライフサイクル
//------------------------------------------------------------------------------------------------------------

//------------------------------------------------------------------------------------------------------------
//watch
//------------------------------------------------------------------------------------------------------------
watch(
  () => props.value,
  (value) => {
    input.value = value
  }
)

watch(
  () => props.error,
  (value) => {
    inputError.value = value
  }
)

watch(
  () => props.data,
  (value) => {
    input.value = value?.value
    if (props.data) {
      if (value?.onCheck === 'check') {
        value.error = ''
        props.data.error = ''
        inputError.value = ''
        validation()
        if (value.value) {
          emits('update:value', String(input.value))
          emits('update:error', inputError.value || '')
        }
        props.data.onCheck = ''
      } else if (value?.onCheck === 'clear') {
        value.error = ''
        props.data.onCheck = ''
        props.data.error = ''
        inputError.value = ''
      }
      if (value?.error) {
        inputError.value = value?.error
      } else {
        inputError.value = ''
      }
    }
  },
  {
    deep: true,
  }
)

watch(
  () => props.edit,
  (value) => {
    if (value) {
      isEdit.value = true
    } else {
      isEdit.value = false
    }
  }
)

watch(
  () => props.base,
  (value) => {
    if (value?.edit) {
      isEdit.value = true
    } else {
      isEdit.value = false
    }
  }
)

//------------------------------------------------------------------------------------------------------------
// エミット
//------------------------------------------------------------------------------------------------------------
const emits = defineEmits<{
  (e: 'update:value', value: string): void
  (e: 'update:blur', value: string): void
  (e: 'update:focus', value: string | null | undefined): void
  (e: 'update:error', value: string): void
}>()

function onChange(e: Event) {
  const target = e.target as HTMLInputElement
  if (target) {
    input.value = target.value
    if (props.data) {
      props.data.value = target.value
    }
    validation()
    emits('update:value', target.value)
    emits('update:error', inputError.value || '')
  }
}

//------------------------------------------------------------------------------------------------------------
// メソッド
//------------------------------------------------------------------------------------------------------------
function onFocus() {
  focus.value = true
  emits('update:focus', input.value)
}
function onBlur() {
  if (input.value) {
    emits('update:blur', input.value)
  }
  focus.value = false
}

//------------------------------------------------------------------------------------------------------------
// バリデーション
//------------------------------------------------------------------------------------------------------------

function validation() {
  if (props.data) props.data.isCheck = true
  const rule: RULE[] = props.rule || props.data?.rule || []
  const ruleParams: ruleParams = {
    format: props.data?.ruleParams?.format,
    info: props.data?.ruleParams?.info,
    min: props.min || props.data?.ruleParams?.min,
    max: props.max || props.data?.ruleParams?.max,
    region: props.data?.ruleParams?.region, //zip-codeや、電話番号の国コード
  }

  const res = useValidation(input.value || '', rule, result, ruleParams)
  if (res === true) {
    inputError.value = ''
    if (props.data) {
      props.data.error = ''
    }
    return true
  } else {
    inputError.value = res
    if (props.data) {
      props.data.error = res
    }

    return false
  }
}
</script>

<style scoped>
.Input {
  position: relative;
}
</style>
