<template>
  <FormBase
    class="FormEMail"
    :base="base"
    :label="label"
    :rule="rule || data?.rule"
    :mode="mode"
    :edit="isEdit"
    :width="width || base?.width"
    :col-width="colWidth"
    :label-width="labelWidth"
    :hint="hint"
    :hintchip="hintchip"
    :error="inputError"
    :result="result"
    :focus="focus"
    :small="small"
    :medium="medium"
    :style="{
      width:
        width || base?.width
          ? undefined
          : mode === FORM_MODE.INLINE || mode === FORM_MODE.SIMPLE
            ? `calc((100% - ${base?.label?.width || 100 + 10}px) / 2 + ${base?.label?.width || 100}px)`
            : `calc((100% - 10px) / 2)`,
    }"
  >
    <div class="FormInputContainer">
      <div v-if="prefix" class="Prefix">{{ prefix }}</div>
      <div
        class="FormInputContent"
        :style="{
          width: width ? width + 'px' : base?.width ? base.width + 'px' : '100%',
        }"
      >
        <template v-if="isEdit">
          <input
            :id="props.data?.name"
            :name="props.data?.name"
            class="Input"
            :class="{ InputError: inputError }"
            ref="inputItem"
            type="text"
            :value="input"
            :placeholder="placeholder"
            @input="onChange"
            @focus="onFocus"
            @blur="onBlur"
          />
        </template>
        <input v-else type="text" class="Preview" :value="input" disabled />
        <ButtonInnerForm
          class="Icon"
          :icon="FORM_ICONS.PHONE"
          :size="16"
          :width="30"
          :align="ALIGN.LEFT"
          :back-color="'transparent'"
          :color="useColor(isDark() ? THEME_COLOR.guide : THEME_COLOR.line)"
        />
      </div>
      <div v-if="suffix" class="Suffix">{{ suffix }}</div>
    </div>
  </FormBase>
</template>

<script setup lang="ts">
/**===================================================================================================================
   *
   ===================================================================================================================**/
import { FORM_MODE, type formProps, type formItem } from '../../components/common'
import { RULE, useValidation } from '../../composables/validation'
import { THEME_COLOR, FORM_ICONS, ALIGN } from '../../composables/constants'
import { isDark, useColor } from '../../composables/colors'
import { ref, watch, type Ref } from 'vue'

//------------------------------------------------------------------------------------------------------------
// 引数
//------------------------------------------------------------------------------------------------------------
interface Props extends formProps {
  rule?: any
  value?: string
}

const props = withDefaults(defineProps<Props>(), {})
//------------------------------------------------------------------------------------------------------------
// 定数・変数（state）
//------------------------------------------------------------------------------------------------------------
const input: Ref<string | undefined> = ref(props.data?.value || props.value)
const inputError: Ref<string | undefined> = ref(props.error)
const inputItem: Ref<HTMLInputElement | undefined> = ref()
const result: Ref<string | undefined> = ref()
const isEdit = ref(props.edit || props.base?.edit)
const focus = ref(false)

//------------------------------------------------------------------------------------------------------------
// ライフサイクル
//------------------------------------------------------------------------------------------------------------

//------------------------------------------------------------------------------------------------------------
//watch
//------------------------------------------------------------------------------------------------------------
watch(
  () => props.value,
  (value) => {
    input.value = value
  }
)

watch(
  () => props.error,
  (value) => {
    inputError.value = value
  }
)

watch(
  () => props.data,
  (value) => {
    input.value = value?.value
    if (props.data) {
      if (value?.onCheck === 'check') {
        validation()
        props.data.onCheck = ''
      } else if (value?.onCheck === 'clear') {
        props.data.onCheck = ''
        props.data.error = ''
        inputError.value = ''
      }
    }
    if (value?.error) {
      inputError.value = value?.error
    }
  },
  {
    deep: true,
  }
)

watch(
  () => props.edit,
  (value) => {
    if (value) {
      isEdit.value = true
    } else {
      isEdit.value = false
    }
  }
)

watch(
  () => props.base,
  (value) => {
    if (value?.edit) {
      isEdit.value = true
    } else {
      isEdit.value = false
    }
  }
)

//------------------------------------------------------------------------------------------------------------
// エミット
//------------------------------------------------------------------------------------------------------------
const emits = defineEmits<{
  (e: 'update:value', value: string): void
  (e: 'update:error', value: string): void
}>()

function onChange(e: Event) {
  const target = e.target as HTMLInputElement
  if (target) {
    input.value = target.value
    if (props.data) {
      props.data.value = target.value
    }
    validation()
    emits('update:value', target.value)
    emits('update:error', inputError.value || '')
  }
}

//------------------------------------------------------------------------------------------------------------
// メソッド
//------------------------------------------------------------------------------------------------------------
function onFocus() {
  focus.value = true
}
function onBlur() {
  focus.value = false
}

//------------------------------------------------------------------------------------------------------------
// バリデーション
//------------------------------------------------------------------------------------------------------------

function validation() {
  if (props.data) props.data.isCheck = true

  //RULEの確認
  const rule: RULE[] = props.rule || props.data?.rule || []
  if (!rule.includes(RULE.phone)) {
    rule.push(RULE.phone)
  }

  const res = useValidation(input.value || '', rule, result, props.data?.ruleParams)
  if (res === true) {
    inputError.value = ''
    if (props.data) props.data.error = ''
    return true
  } else {
    inputError.value = res
    if (props.data) props.data.error = res
    return false
  }
}
</script>

<style scoped>
.Input {
  position: relative;
  padding-left: 28px !important;
}

.Preview {
  position: relative;
  padding-left: 28px !important;
}

.Icon {
  position: absolute;
  top: 0px;
  left: 0px;
  cursor: default !important;
}
</style>
