<template>
  <FormBase
    class="FormRadioGroup"
    :base="base"
    :label="label"
    :rule="rule || data?.rule"
    :mode="mode"
    :edit="isEdit"
    :width="width"
    :col-width="colWidth"
    :label-width="labelWidth"
    :hint="hint"
    :hintchip="hintchip"
    :error="inputError"
    :result="result"
    :focus="focus"
    :line="false"
    :small="small"
    :medium="medium"
  >
    <ButtonRadioSet
      class="mt-1"
      :items="items"
      :value="input"
      :inline="inline"
      :color="color"
      :disabled="!isEdit"
      @update:value="onChange($event)"
    />
  </FormBase>
</template>

<script setup lang="ts">
/**===================================================================================================================
   * InputText(文字列一行入力)
   ===================================================================================================================**/
import { FORM_MODE, type formProps, type formItem } from '../../components/common'
import { type ListButtonProps } from '../../components/common'
import { RULE, useValidation } from '../../composables/validation'
// 2024.01.31 モジュール化に伴い、コメントアウト
// import { ItemsApi } from '~/types/api'
import { ref, watch, onBeforeMount, type Ref, onMounted } from 'vue'

//------------------------------------------------------------------------------------------------------------
// 引数
//------------------------------------------------------------------------------------------------------------
interface Props extends formProps {
  rule?: any
  value?: string
  items?: ListButtonProps[]
  color?: string
  inline?: boolean
}

const props = withDefaults(defineProps<Props>(), {})
//------------------------------------------------------------------------------------------------------------
// 定数・変数（state）
//------------------------------------------------------------------------------------------------------------
const input: Ref<string | undefined> = ref(props.data?.value || props.value)
const inputError: Ref<string | undefined> = ref(props.error)
const result: Ref<string | undefined> = ref()
const focus = ref(false)
const isEdit = ref(props.edit || props.base?.edit)

//------------------------------------------------------------------------------------------------------------
// ライフサイクル
//------------------------------------------------------------------------------------------------------------
//------------------------------------------------------------------------------------------------------------
// ライフサイクル
//------------------------------------------------------------------------------------------------------------
onMounted(() => {
  setDefault()
})

function setDefault() {
  if (!input.value && props.items) {
    for (let i = 0; i < props.items.length; i++) {
      const item = props.items[i]
      if (item.default) {
        input.value = item.value
        if (props.data) {
          props.data.value = item.value
        }
        break
      }
    }
    if (props.data) {
      props.data.isCheck = true
    }
    // console.log('ラジオグループの初期化', input.value)
  }
}

//------------------------------------------------------------------------------------------------------------
//watch
//------------------------------------------------------------------------------------------------------------
watch(
  () => props.value,
  (value) => {
    input.value = value
  }
)

watch(
  () => props.error,
  (value) => {
    inputError.value = value
  }
)

watch(
  () => props.data,
  (value) => {
    input.value = value?.value
    setDefault()
    if (props.data) {
      if (value?.onCheck === 'check') {
        validation()
        props.data.onCheck = ''
      } else if (value?.onCheck === 'clear') {
        props.data.onCheck = ''
        props.data.error = ''
        inputError.value = ''
      }
    }
    if (value?.error) {
      inputError.value = value?.error
    }
  },
  {
    deep: true,
  }
)

watch(
  () => props.edit,
  (value) => {
    if (value) {
      isEdit.value = true
    } else {
      isEdit.value = false
    }
  }
)

watch(
  () => props.base,
  (value) => {
    if (value?.edit) {
      isEdit.value = true
    } else {
      isEdit.value = false
    }
  }
)

//------------------------------------------------------------------------------------------------------------
// エミット
//------------------------------------------------------------------------------------------------------------
const emits = defineEmits<{
  (e: 'update:value', value: string): void
  (e: 'update:error', value: string): void
}>()

function onChange(value: any) {
  // console.log('Radio', value)

  input.value = value
  if (props.data) {
    props.data.value = value
  }
  validation()
  emits('update:value', value)
  emits('update:error', inputError.value || '')
}

function validation() {
  if (props.data) props.data.isCheck = true
  const rule: RULE[] = props.rule || props.data?.rule || []

  if (rule.includes(RULE.required)) {
    //選択項目に値が無ければerror
    const item = props.items?.find((item) => item.value === input.value)
    if (!item) {
      inputError.value = '必須項目です。'
      if (props.data) props.data.error = '必須項目です。'
      return false
    }
  }

  const res = useValidation(input.value || '', rule, result, props.data?.ruleParams)
  if (res === true) {
    inputError.value = ''
    if (props.data) props.data.error = ''
    return true
  } else {
    inputError.value = res
    if (props.data) props.data.error = res
    return false
  }
}

//------------------------------------------------------------------------------------------------------------
// メソッド
//------------------------------------------------------------------------------------------------------------
function onFocus() {
  focus.value = true
}
function onBlur() {
  focus.value = false
}

//------------------------------------------------------------------------------------------------------------
// バリデーション
//------------------------------------------------------------------------------------------------------------
</script>

