<template>
  <FormBase
    class="FormSelectMenu"
    :base="base"
    :label="label"
    :rule="rule || data?.rule"
    :mode="mode"
    :edit="isEdit"
    :width="width"
    :col-width="colWidth"
    :label-width="labelWidth"
    :hint="hint"
    :hintchip="hintchip"
    :error="inputError"
    :result="result"
    :focus="focus"
    :line="false"
    :small="small"
    :medium="medium"
  >
    <PartsSuggestMenu
      :value="input"
      :inputType="inputType"
      :selectType="selectType"
      :items="items"
      :height="height"
      :disabled="!isEdit"
      :error="inputError"
      :noNewLink="noNewLink"
      fit
      @update:value="onChange($event)"
      @update:suggest-create="onSuggestCreate($event)"
    />
  </FormBase>
</template>

<script setup lang="ts">
/**
 * Selectメニューのフォーム用ラッパー
 **/
import { FORM_MODE, type formProps, type formItem } from '../../components/common'
import { SELECT_INPUT_TYPE, SELECT_LIST_TYPE } from '../../components/common'
import { RULE, useValidation } from '../../composables/validation'
import { ref, watch, type Ref } from 'vue'

interface Props extends formProps {
  items: any[]
  value?: string
  inputType?: SELECT_INPUT_TYPE
  selectType?: SELECT_LIST_TYPE
  height?: number
  color?: string
  rule?: any
  noNewLink?: boolean
}

const props = withDefaults(defineProps<Props>(), {
  inputType: SELECT_INPUT_TYPE.INPUT,
  selectType: SELECT_LIST_TYPE.DEFAULT,
})

const inputError: Ref<string | undefined> = ref(props.error)
const result: Ref<string | undefined> = ref()
const focus = ref(false)
const isEdit = ref(props.edit || props.base?.edit)

//------------------------------------------------------------------------------------------------------------
//watch
//------------------------------------------------------------------------------------------------------------
watch(
  () => props.value,
  (value) => {
    input.value = value
  }
)

watch(
  () => props.error,
  (value) => {
    inputError.value = value
  }
)

watch(
  () => props.data,
  (value) => {
    input.value = value?.value
    if (props.data) {
      if (value?.onCheck === 'check') {
        validation()
        props.data.onCheck = ''
      } else if (value?.onCheck === 'clear') {
        props.data.onCheck = ''
        props.data.error = ''
        inputError.value = ''
      }
    }
    if (value?.error) {
      inputError.value = value?.error
    }
  },
  {
    deep: true,
  }
)

watch(
  () => props.edit,
  (value) => {
    if (value) {
      isEdit.value = true
    } else {
      isEdit.value = false
    }
  }
)

watch(
  () => props.base,
  (value) => {
    if (value?.edit) {
      isEdit.value = true
    } else {
      isEdit.value = false
    }
  }
)

//------------------------------------------------------------------------------------------------------------
// エミット
//------------------------------------------------------------------------------------------------------------
const emits = defineEmits<{
  (e: 'update:value', value: string): void
  (e: 'update:error', value: string): void
  (e: 'update:suggestCreate', value: string): void
}>()

const input = ref(props.value || props.data?.value)

function onChange(value: any) {
  input.value = value
  if (props.data) {
    props.data.value = value
  }
  validation()
  emits('update:value', value)
  emits('update:error', inputError.value || '')
}

function onSuggestCreate(value: any) {
  emits('update:suggestCreate', value)
}

//------------------------------------------------------------------------------------------------------------
// バリデーション
//------------------------------------------------------------------------------------------------------------
function validation() {
  if (props.data) props.data.isCheck = true
  const rule: RULE[] = props.rule || props.data?.rule || []

  const res = useValidation(String(input.value), rule, result, props.data?.ruleParams)
  if (res === true) {
    inputError.value = ''
    if (props.data) props.data.error = ''
    return true
  } else {
    inputError.value = res
    if (props.data) props.data.error = res
    return false
  }
}
</script>
<style lang="scss" scoped></style>
