<template>
  <div
    v-if="mask.state.lock"
    class="Mask"
    :style="{
      top: getRect().top,
      left: getRect().left,
      width: getRect().width,
      height: getRect().height,
      borderRadius: getRect().radius,
      cursor: mask.state.cursor,
    }"
  />
</template>

<script setup lang="ts">
import { useActiveRect, useMask } from '../../composables/utility'
//------------------------------------------------------------------------------------------------------------
// 定数・変数（state）
//------------------------------------------------------------------------------------------------------------
const mask = useMask()

//------------------------------------------------------------------------------------------------------------
// メソッド
//------------------------------------------------------------------------------------------------------------
function getRect() {
  return useActiveRect(0)
}
</script>

<style scoped>
.Mask {
  position: fixed;
  background-color: rgba(0, 0, 0, 0);
  display: flex;
  justify-content: center;
  align-items: center;
}
</style>
