import { ref } from "vue";
import { defineStore } from "pinia";
export const useListColumnSetting = defineStore(
  "list-column-setting",
  () => {
    const state = ref({});
    return {
      value: state.value
    };
  },
  {
    persist: process.client && {
      storage: localStorage
    }
  }
);
