import { useState } from "nuxt/app";
export const useSnackbarList = () => {
  const state = useState("snackbar-list", () => ({
    counter: 0,
    list: []
  }));
  function setItem(value) {
    state.value.counter++;
    value.id = state.value.counter;
    state.value.list.push(value);
  }
  function deleteItem(value) {
    const index = state.value.list.findIndex((item) => item.id === value.id);
    if (index !== -1) {
      state.value.list.splice(index, 1);
    }
  }
  return {
    list: state.value.list,
    setItem,
    deleteItem
  };
};
