import { useState } from "nuxt/app";
export const useLoginStatus = () => useState("login-status", () => {
  return {
    isRemember: false
  };
});
export const useHeaderStatus = () => useState("header-status", () => {
  return {
    height: 45,
    visible: true
  };
});
export const useSubHeaderStatus = () => useState("sub-header-status", () => {
  return {
    height: 36,
    visible: true
  };
});
export const useFooterStatus = () => useState("footer-status", () => {
  return {
    height: 32,
    visible: true
  };
});
export const useMainNavigationStatus = () => useState("main-navigation-status", () => {
  return {
    width: 180,
    visible: true,
    menuType: "test",
    open: true
  };
});
export const useLocalMenuStatus = () => useState("local-menu-status", () => {
  return {
    width: 200,
    open: true
  };
});
