import { ref } from "vue";
import { defineStore } from "pinia";
export const useTenantSetting = defineStore(
  "tenant-setting",
  () => {
    const state = ref({
      id: "",
      name: "",
      key: "",
      tenantTab: "information",
      projectTab: "information"
    });
    return {
      value: state
    };
  },
  {
    persist: process.client && {
      storage: localStorage
    }
  }
);
